const socket = io.connect("http://localhost:80");
function App() {
  const dispatch = useDispatch();
  const store = useSelector(state => state);

  useEffect(() => {
    dispatch(initSocket(socket));
    return () => {
      socket.close();
    };
  }, []);

  useEffect(() => {
    store.socket.on('message', (msg) => {
      console.log('message send')
      console.log(msg);
    });
  }, [store.socket])

  return (
    <Login />
  );
}

export default App;
