import MainChatInputPresenter from "../Presenter/MainChatInputPresenter";
import { InputGroup, FormControl, Button } from "react-bootstrap";
import styled from "styled-components";

const InputGroupSize = styled(InputGroup)`
    display:flex;
    align-items:center;
    justify-content:center;
    width: 100%;
    height: 80%;
`

const FormControlCustom = styled(FormControl)`
    width: 90%;
    height: 100px;
    word-break:break-all
`

const MainChatInputContainer = () => {
    return (
        <MainChatInputPresenter>
            <InputGroupSize className="mb-3" >
                <FormControlCustom as="textarea"
                    placeholder="Recipient's username"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                />
                <Button variant="outline-secondary" id="button-addon2">
                    send
                </Button>
            </InputGroupSize>
        </MainChatInputPresenter>
    )
}

export default MainChatInputContainer;
