let socket;
const initsocket = async () => {
  socket = await io.connect("http://localhost:80");
  await socket.on("message", (msg) => {
    console.log('message send')
    console.log(msg);
  });
  console.log(socket);
}

function App() {
  const dispatch = useDispatch();
  const store = useSelector(state => state);

  useEffect(() => {
    initsocket();
    dispatch(initSocket(socket));
    return () => {
      socket.close();
    };
  }, []);



  return (
    <Login />
  );
}

export default App;
