import produce from 'immer';

const ADD_ROOM = 'roomAndChannel/ADD_ROOM';
const SELECT_ROOM = 'roomAndChannel/SELECT_ROOM';
const ADD_CHANNEL = 'roomAndChannel/ADD_CHANNEL';
const SELECT_CHANNEL = 'roomAndChannel/SELECT_CHANNEL';

export const addRoom = roomName => ({
    type: ADD_ROOM,
    room: {
        id: nextRoomId++,
        name: roomName,
        channels: [],
    },
})

export const selectRoom = roomId => ({
    type: SELECT_ROOM,
    id: roomId,
})

export const addChannel = (channelName) => ({
    type: ADD_CHANNEL,
    channel: {
        id: nextChannelId++,
        name: channelName,
    }
})

export const selectChannel = channelId => ({
    type: SELECT_CHANNEL,
    id: channelId,
})

let nextRoomId = 3;
let nextChannelId = 3;

const initialState = {
    selectRoom: 1,
    selectChannel: 1,
    rooms: [
        {
            id: 1,
            name: 'first room',
            channels: [
                {
                    id: 1,
                    name: '1-1 channel'
                },
                {
                    id: 2,
                    name: '1-2 channel'
                },
            ]
        },
        {
            id: 2,
            name: 'second room',
            channels: [
                {
                    id: 1,
                    name: '2-1 channel'
                },
                {
                    id: 2,
                    name: '2-2 channel'
                },
            ]
        },
    ],
};

export default function roomAndChannel(state = initialState, action) {
    switch (action.type) {
        case ADD_ROOM:
            return { ...state, rooms: state.rooms.concat(action.room) };
        case SELECT_ROOM:
            return { ...state, selectRoom: action.id };
        case ADD_CHANNEL:
            return produce(state, draft => {
                draft.rooms[state.selectRoom - 1].channels.push(action.channel);
            })
        default:
            return state;
    }
}
